import React from "react";
import Square from "./Square";
import ColoredSquare from "./Square";

class Board extends React.Component {
  // Create the 3 x 3 board
  createBoard(row, col) {
    const board = [];
    let cellCounter = 0;

    for (let i = 0; i < row; i += 1) {
      const columns = [];
      for (let j = 0; j < col; j += 1) {
        columns.push(this.renderSquare(cellCounter++));
      }
      board.push(
        <div key={i} className="board-row">
          {columns}
        </div>
      );
    }

    return board;
  }

  renderSquare(i) {
    return (
      <Square
        key={i}
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }
  renderColoredSquare(i) {
    return (
      <ColoredSquare
        key={i}
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  render() {
    return (
      <div>
        <img
          className="emptyBoard"
          src="https://i.imgur.com/zf9ddV9.png"
          alt="emptyBoard"
          widt="150px"
          heigh="150px"
        ></img>
        <div className="sushi1"></div>
        <div className="sushi2"></div>
        <div className="sushi3"></div>
        <div className="sushi4"></div>
      </div>
    );
  }
}

export default Board;
