import React from "react";
import Square from "./Square";
import ColoredSquare from "./Square";

class Board extends React.Component {
  // Create the 3 x 3 board
  createBoard(row, col) {
    const board = [];
    let cellCounter = 0;

    for (let i = 0; i < row; i += 1) {
      const columns = [];
      for (let j = 0; j < col; j += 1) {
        columns.push(this.renderSquare(cellCounter++));
      }
      board.push(
        <div key={i} className="board-row">
          {columns}
        </div>
      );
    }

    return board;
  }

  renderSquare(i) {
    return (
      <Square
        key={i}
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }
  renderColoredSquare(i) {
    return (
      <ColoredSquare
        key={i}
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  render() {
    return (
      <div id="backgroundBoard">
        <img
          id="emptyBoard"
          src="https://i.imgur.com/LNH3flq.png"
          alt="board0"
          hidden={5 > 0}
        ></img>
        <img
          id="fuguBoard"
          src="https://i.imgur.com/xBtGEro.png"
          alt="board1"
          hidden={5 > 0}
        ></img>
        <img
          id="fuguBoard"
          src="https://i.imgur.com/v6qEAq9.png"
          alt="board2"
          hidden={5 > 0}
        ></img>
        <img
          id="fuguBoard"
          src="https://i.imgur.com/Msw4WCu.png"
          alt="board3"
        ></img>
        <img
          id="fuguBoard"
          src="https://i.imgur.com/656n59P.png"
          alt="board4"
          hidden={5 > 0}
        ></img>
      </div>
    );
  }
}

export default Board;
