import React, { Component } from "react";
import Game from "./Game";
import Board from "./Board";
import PubNubReact from "pubnub-react";
import Swal from "sweetalert2";
import shortid from "shortid";
import "./Game.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.pubnub = new PubNubReact({
      publishKey: "pub-c-fae6dfb9-0fd1-4b5e-a0f3-a3d4511085e9",
      subscribeKey: "sub-c-0b5e637c-3675-11eb-b6eb-96faa39b9528"
    });

    this.state = {
      piece: "",
      sushis: [],
      placed: [],
      isPlaying: false,
      isRoomCreator: false,
      isDisabled: false,
      myTurn: false
    };

    this.lobbyChannel = null;
    this.gameChannel = null;
    this.roomId = null;
    this.pubnub.init(this);
  }

  componentWillUnmount() {
    this.pubnub.unsubscribe({
      channels: [this.lobbyChannel, this.gameChannel]
    });
  }

  componentDidUpdate() {
    // Check that the player is connected to a channel
    if (this.lobbyChannel != null) {
      this.pubnub.getMessage(this.lobbyChannel, msg => {
        // Start the game once an opponent joins the channel
        if (msg.message.notRoomCreator) {
          // Create a different channel for the game
          this.gameChannel = "fuguegame--" + this.roomId;

          this.pubnub.subscribe({
            channels: [this.gameChannel]
          });

          this.setState({
            isPlaying: true
          });

          // Close the modals if they are opened
          Swal.close();

          (async () => {
            /* inputOptions can be an object or Promise */
            const inputOptions = new Promise(resolve => {
              setTimeout(() => {
                resolve({
                  true: "Normal sushi",
                  false: "Fugu sushi"
                });
              }, 1000);
            });

            const { value: color } = await Swal.fire({
              title: "Select color",
              input: "radio",
              inputOptions: inputOptions,
              inputValidator: value => {
                if (!value) {
                  return "You need to choose something!";
                }
              }
            });

            if (color) {
              Swal.fire({ html: `You selected: ${color}` });
            }
          })();
        }
      });
    }
  }

  // Create a room channel
  onPressCreate = e => {
    // Create a random name for the channel
    this.roomId = shortid.generate().substring(0, 5);
    this.lobbyChannel = "fugulobby--" + this.roomId;

    this.pubnub.subscribe({
      channels: [this.lobbyChannel],
      withPresence: true
    });

    // Open the modal
    Swal.fire({
      position: "top",
      allowOutsideClick: false,
      title: "Share this room ID with your friend",
      text: this.roomId,
      width: 275,
      padding: "0.7em",
      // Custom CSS
      customClass: {
        heightAuto: false,
        title: "title-class",
        popup: "popup-class",
        confirmButton: "button-class"
      }
    });

    this.setState({
      piece: "X",
      sushis: [true, true, true, false],
      isRoomCreator: true,
      isDisabled: true, // Disable the 'Create' button
      myTurn: true // Room creator makes the 1st move
    });
  };

  // The 'Join' button was pressed
  onPressJoin = e => {
    Swal.fire({
      position: "top",
      input: "text",
      allowOutsideClick: false,
      inputPlaceholder: "Enter the room id",
      showCancelButton: true,
      confirmButtonColor: "rgb(208,33,41)",
      confirmButtonText: "OK",
      width: 275,
      padding: "0.7em",
      customClass: {
        heightAuto: false,
        popup: "popup-class",
        confirmButton: "join-button-class ",
        cancelButton: "join-button-class"
      }
    }).then(result => {
      // Check if the user typed a value in the input field
      if (result.value) {
        this.joinRoom(result.value);
      }
    });
  };

  // Join a room channel
  joinRoom = value => {
    this.roomId = value;
    this.lobbyChannel = "fugulobby--" + this.roomId;

    // Check the number of people in the channel
    this.pubnub
      .hereNow({
        channels: [this.lobbyChannel]
      })
      .then(response => {
        if (response.totalOccupancy < 2) {
          this.pubnub.subscribe({
            channels: [this.lobbyChannel],
            withPresence: true
          });

          this.setState({
            piece: "O",
            sushis: [true, true, true, false]
          });

          this.pubnub.publish({
            message: {
              notRoomCreator: true
            },
            channel: this.lobbyChannel
          });
        } else {
          // Game in progress
          Swal.fire({
            position: "top",
            allowOutsideClick: false,
            title: "Error",
            text: "Game in progress. Try another room.",
            width: 275,
            padding: "0.7em",
            customClass: {
              heightAuto: false,
              title: "title-class",
              popup: "popup-class",
              confirmButton: "button-class"
            }
          });
        }
      })
      .catch(error => {
        console.log(error);
      });
  };

  // Reset everything
  endGame = () => {
    this.setState({
      piece: "",
      isPlaying: false,
      isRoomCreator: false,
      isDisabled: false,
      myTurn: false
    });

    this.lobbyChannel = null;
    this.gameChannel = null;
    this.roomId = null;

    this.pubnub.unsubscribe({
      channels: [this.lobbyChannel, this.gameChannel]
    });
  };

  render() {
    return (
      <div>
        <div className="title">
          <p>React Tic Tac Toe</p>
        </div>
        <div className="gamePin">
          <p>Gamepin: {this.roomId} </p>
        </div>

        {!this.state.isPlaying && (
          <div className="game">
            <div className="board">
              <Board squares={0} onClick={index => null} />

              <div className="button-container">
                <button
                  className="create-button "
                  disabled={this.state.isDisabled}
                  onClick={e => this.onPressCreate()}
                >
                  {" "}
                  Create
                </button>
                <button
                  className="join-button"
                  onClick={e => this.onPressJoin()}
                >
                  {" "}
                  Join
                </button>
              </div>
            </div>
          </div>
        )}

        {this.state.isPlaying && (
          <Game
            pubnub={this.pubnub}
            gameChannel={this.gameChannel}
            piece={this.state.piece}
            isRoomCreator={this.state.isRoomCreator}
            myTurn={this.state.myTurn}
            xUsername={this.state.xUsername}
            oUsername={this.state.oUsername}
            endGame={this.endGame}
          />
        )}
      </div>
    );
  }
}

export default App;
