import React from "react";
import Board from "./Board";
import Swal from "sweetalert2";

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(""), // 3x3 board
      xScore: 0,
      oScore: 0,
      whosTurn: this.props.myTurn,
      bidding: false,
      passBidding: false,
      lastBidding: false

      //occupancy: this.props.pubnub.hereNow({
      //channels: this.props.gameChannel
      //})
    };

    this.turn = 0;
    this.whosBidding = null;
    this.gameOver = false;
    this.counter = 0;
    this.bid = 0;
  }

  componentDidMount() {
    this.props.pubnub.getMessage(this.props.gameChannel, msg => {
      if (msg.message.turn < 1000) {
        console.log("vi er inne i den nye IFffen");
        this.turn = msg.message.turn;
        if (this.props.players[msg.message.turn] === this.props.userName) {
          this.myMove(msg.message.index, msg.message.piece);
        }
      } else if (msg.message.bidding) {
        if (msg.message.amountEaten > 0) {
          this.publishBidding(msg.message.amountEaten, msg.message.ateBy);
          console.log(
            "Player " + msg.message.ateBy + "Ate " + msg.message.amountEaten
          );
          this.publishBidding(msg.message.amountEaten, msg.message.ateBy);
        } else if (msg.message.amountEaten === null) {
          console.log("Last player passed");
          if (msg.message.lastAmount === this.bid) {
            console.log("du er den siste som er igjen");
            this.setState({
              lastBidding: true
            });
            this.announceEating(this.userName, this.bid);
          }
          this.publishBidding(msg.message.lastAmount, msg.message.ateBy);
        } else {
          console.log(
            "Vi er inne i elsen og denne spilleren skal begynne Ã¥ spise " +
              this.whosBidding
          );
          this.setState({
            bidding: true
          });
        }

        console.log("LETS START BIDDING!");
      }

      // Start a new round
      else if (msg.message.reset) {
        this.setState({
          squares: Array(9).fill(""),
          whosTurn: this.props.myTurn
        });

        this.turn = 0;
        this.gameOver = false;
        this.counter = 0;
        Swal.close();
      }

      // End the game and go back to the lobby
      else if (msg.message.endGame) {
        Swal.close();
        this.props.endGame();
      }
    });
  }

  newRound = winner => {
    let title = winner === null ? "Tie game!" : `Player ${winner} won!`;
    // Show this if the player is not the room creator
    if (this.props.isRoomCreator === false && this.gameOver) {
      Swal.fire({
        position: "top",
        allowOutsideClick: false,
        title: title,
        text: "Waiting for a new round...",
        confirmButtonColor: "rgb(208,33,41)",
        width: 275,
        customClass: {
          heightAuto: false,
          title: "title-class",
          popup: "popup-class",
          confirmButton: "button-class"
        }
      });
      this.turn = 0; // Set turn to 1 so Player 2 can't make a move
    }

    // Show this to the room creator
    else if (this.props.isRoomCreator && this.gameOver) {
      Swal.fire({
        position: "top",
        allowOutsideClick: false,
        title: title,
        text: "Continue Playing?",
        showCancelButton: true,
        confirmButtonColor: "rgb(208,33,41)",
        cancelButtonColor: "#aaa",
        cancelButtonText: "Nope",
        confirmButtonText: "Yea!",
        width: 275,
        customClass: {
          heightAuto: false,
          title: "title-class",
          popup: "popup-class",
          confirmButton: "button-class",
          cancelButton: "button-class"
        }
      }).then(result => {
        // Start a new round
        if (result.value) {
          this.props.pubnub.publish({
            message: {
              reset: true
            },
            channel: this.props.gameChannel
          });
        } else {
          // End the game
          this.props.pubnub.publish({
            message: {
              endGame: true
            },
            channel: this.props.gameChannel
          });
        }
      });
    }
  };
  announceEating = (player, amount) => {
    this.props.pubnub.publish({
      message: {
        whoEating: player,
        amount: amount
      }
    });
  };

  announceBidding = (e, player, lastAmount) => {
    //tell other players the game is moving into the bidding phase
    this.props.pubnub.publish({
      message: {
        bidding: true,
        amountEaten: e,
        ateBy: player,
        lastAmount: lastAmount
      },
      channel: this.props.gameChannel
    });
  };

  // Update score for the winner
  announceWinner = winner => {
    let pieces = {
      X: this.state.xScore,
      O: this.state.oScore
    };

    if (winner === "X") {
      pieces["X"] += 1;
      this.setState({
        xScore: pieces["X"]
      });
    } else {
      pieces["O"] += 1;
      this.setState({
        oScore: pieces["O"]
      });
    }
    // End the game once there is a winner
    this.gameOver = true;
    this.newRound(winner);
  };

  checkForWinner = squares => {
    // Possible winning combinations
    const possibleCombinations = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];

    // Iterate every combination to see if there is a match
    for (let i = 0; i < possibleCombinations.length; i += 1) {
      const [a, b, c] = possibleCombinations[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        this.announceWinner(squares[a]);
        return;
      }
    }

    // Check if the game ends in a draw
    this.counter++;
    // The board is filled up and there is no winner
    if (this.counter === 9) {
      this.gameOver = true;
      this.newRound(null);
    }
  };

  // Opponent's move is annpunced
  myMove = e => {
    //this.turn = this.turn === 1 ? 2 : 1;
    this.setState({
      whosTurn: !this.state.whosTurn
    });
  };

  publishBidding = (e, player) => {
    //Setter whos bidding til neste som skal spise
    this.whosBidding =
      player + 1 === this.props.players.length ? 0 : player + 1;
    if (this.whosBidding === this.props.players.indexOf(this.props.userName)) {
      if (this.state.passBidding) {
        this.announceBidding(null, this.whosBidding, e);
        this.setState({
          whosTurn: !this.state.whosTurn
        });
      } else {
        this.eat(e, this.whosBidding);
      }
    } else {
      console.log("Noen andre skal spise akkuratt nÃ¥");
    }
  };

  onMakeMove = sushi => {
    //const placed = this.props.placed;
    console.log("We are about to make a move");

    // Check if the square is empty and if it's the player's turn to make a move

    //squares[index] = this.props.piece;

    this.setState({
      whosTurn: !this.state.whosTurn
    });
    console.log("now this player is making a move" + this.turn);
    console.log("the total capacity is" + this.props.occupancy);

    // Other player's turn to make a move
    this.turn = this.turn + 1 === this.props.players.length ? 0 : this.turn + 1;
    console.log(
      " next move will be made by player " + this.props.players[this.turn]
    );

    // Publish move to the channel
    this.props.pubnub.publish({
      message: {
        piece: this.props.piece,
        turn: this.turn
      },
      channel: this.props.gameChannel
    });
    console.log("We made a move");

    //Check if there is a winner
    //this.checkForWinner(squares);
  };
  eat = (e, player) => {
    const a = parseInt(e) + 1;
    Swal.fire({
      title: "How many pieces do you think you can eat?",
      input: "number",
      showCancelButton: true,
      cancelButtonText: "Pass",
      cancelButtonColor: "Orange",
      inputAttributes: {
        min: a
      },
      confirmButtonText: "Eat",
      allowOutsideClick: false
    }).then(result => {
      if (result.value) {
        console.log("DU mener du kan spise " + result.value);
        this.bid = result.value;
        this.announceBidding(result.value, player);
      } else {
        this.setState({
          passBidding: true,
          bidding: false
        });
        console.log("Du har passet");
        this.announceBidding(null, player, e);
      }
    });
  };

  showChoices = e => {
    (async () => {
      /* inputOptions can be an object or Promise */
      const inputOptions = new Promise(resolve => {
        setTimeout(() => {
          resolve({
            true: "Normal sushi",
            false: "Fugu sushi",
            eat: "Start bidding!"
          });
        }, 1000);
      });

      const { value: sushi } = await Swal.fire({
        title: "What sushi would you like to place?",
        input: "radio",
        inputOptions: inputOptions,
        allowOutsideClick: false,
        allowEscapeKey: false,
        confirmButtonText: "Place sushi",
        inputValidator: value => {
          if (!value) {
            return "You need to choose something!";
          } else {
            if (value === "true" && !this.props.sushis.includes(true)) {
              return "You do not have enough of this type of sushi!";
            }

            if (value === "false" && !this.props.sushis.includes(false)) {
              return "You do not have enough of this type of sushi!";
            }
          }
        }
      });

      if (sushi) {
        if (sushi === "eat") {
          this.whosBidding = this.turn;
          this.announceBidding(0);
          this.setState({
            bidding: true
          });
          this.eat(0, this.whosBidding);
        } else {
          if (sushi === "true") {
            this.props.placed.push(true);
            this.props.sushis.shift();
            console.log("this is what you have placed " + this.props.placed);
            console.log("And this is what you have left " + this.props.sushis);
          }
          if (sushi === "false") {
            this.props.placed.push(false);
            this.props.sushis.pop();
            console.log("this is what you have placed" + this.props.placed);
            console.log("And this is what you have left " + this.props.players);
          }
          this.onMakeMove(sushi);
          //Swal.fire({ html: `you selected ${sushi}` });
          console.log(this.state.placed);
          console.log(this.state.sushis);
        }
      }
    })();
  };

  render() {
    let status;
    // Change to current player's turn
    status = `${this.state.whosTurn ? "Your turn" : "Opponent's turn"}`;

    return (
      <div className="game">
        <div className="board">
          <Board
            squares={this.state.squares}
            onClick={index => this.onMakeMove(index)}
            placed={this.props.placed}
          />
          <div id="functions">
            <p className="status-info">{status}</p>
            <button
              className="move-button"
              disabled={!this.state.whosTurn}
              onClick={e => this.showChoices()}
              hidden={this.state.bidding || this.state.passBidding}
            >
              {" "}
              Make move
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default Game;
